        instructions = TaskFamily.get_instructions(t)
        criteria = ["The story should be set in the given historical context.", "The story should incorporate accurate historical facts.", "The character should be well-developed and interact naturally within the historical setting.", "The story should be coherent and engaging."]
        return 1.0 if eval_with_llm_judge(instructions, submission, criteria) else 0.0
"
}
