  "task_family": "import random

class TaskFamily:
    @staticmethod
    def get_tasks() -> dict[str, dict]:
        scenarios = [
            {
                "initial_language": "Zorbish",
                "time_period": "500 years",
                "factors": ["isolation", "technological advancement", "climate change"]
            },
            {
                "initial_language": "Nexian",
                "time_period": "1000 years",
                "factors": ["cultural exchange", "political upheaval", "religious shift"]
            }
        ]
        return {
            "1": random.choice(scenarios),
            "2": random.choice(scenarios)
        }

    @staticmethod
    def get_instructions(t: dict) -> str:
        return f"Simulate the evolution of the fictional language '{t['initial_language']}' over a period of {t['time_period']}, considering the following factors: {', '.join(t['factors'])}. Your response should include:

1. Three significant changes in the language's phonology, morphology, or syntax. Explain each change and its potential cause.
2. Five new words that have been added to the language, including their meanings and etymologies.
3. Two grammatical structures that have been lost or significantly altered, and the reasons for these changes.

Ensure your response is coherent and considers how the given factors might realistically influence language evolution. Be creative but ground your answers in plausible linguistic principles."

    @staticmethod
    def score(t: dict, submission: str) -> float | None:
        from src.eval_helper import eval_with_llm_judge
        instructions = TaskFamily.get_instructions(t)
        criteria = [
            "The response includes three significant language changes with explanations and potential causes.",
            "Five new words are presented with meanings and etymologies.",
            "Two grammatical structures that have been lost or altered are described with reasons.",
            "The language evolution considers the given factors and time period.",
            "The response demonstrates understanding of linguistic principles and creative application of language evolution concepts."
        ]
        return 1.0 if eval_with_llm_judge(instructions, submission, criteria) else 0.0
