  "estimated_human_difficulty": "5",
  "done": "False",
  "task_family": "import random

class TaskFamily:
    @staticmethod
    def get_tasks() -> dict[str, dict]:
        tasks = [
            {
                "type": "generate",
                "concept": "time is money",
                "style": "minimalist"
            },
            {
                "type": "interpret",
                "image_description": "A tree with books as leaves growing from a human brain",
                "options": ["Knowledge growth", "Mental library", "Organic learning", "Brain foliage"]
            }
        ]
        return {str(i+1): task for i, task in enumerate(random.sample(tasks, k=2))}

    @staticmethod
    def get_instructions(t: dict) -> str:
        if t['type'] == 'generate':
            return f"Generate a detailed description of a visual metaphor for the concept '{t['concept']}' in a {t['style']} style. Your description should include:\n1. The main visual elements\n2. Their arrangement and composition\n3. How these elements metaphorically represent the concept\n4. Any relevant colors, textures, or other visual details\n5. An explanation of why this visual metaphor effectively conveys the concept"
        elif t['type'] == 'interpret':
            return f"Interpret the following visual metaphor:\n\n{t['image_description']}\n\nChoose the most appropriate metaphorical meaning from these options:\n{', '.join(t['options'])}\n\nExplain your choice, discussing:\n1. How the visual elements relate to the chosen meaning\n2. Why this interpretation is more fitting than the other options\n3. Any additional insights or implications of this visual metaphor"

    @staticmethod
    def score(t: dict, submission: str) -> float | None:
        from src.eval_helper import eval_with_llm_judge
        instructions = TaskFamily.get_instructions(t)
        criteria = [
            "The response demonstrates a deep understanding of visual metaphors and their interpretation",
            "The explanation is clear, coherent, and well-reasoned",
            "The response shows creativity and insight in generating or interpreting the visual metaphor",
            "The answer addresses all parts of the question comprehensively"
        ]
        if t['type'] == 'interpret':
            criteria.append(f"The chosen option is '{t['options'][0]}' (the correct answer)")
        return 1.0 if eval_with_llm_judge(instructions, submission, criteria) else 0.0
"
}
