    def score(t: dict, submission: str) -> float | None:
        from src.eval_helper import eval_with_llm_judge
        instructions = TaskFamily.get_instructions(t)
        criteria = [
            "The numerical system design is coherent, creative, and reflects the given alien civilization's characteristics.",
            "The explanation of mathematical operations is clear and consistent with the designed numerical system.",
            "The complex mathematical problem and its solution are relevant to the alien civilization and correctly use the designed numerical system.",
            "The comparative analysis demonstrates insightful understanding of numerical systems and their implications for mathematical and scientific advancement."
        ]
        return 1.0 if eval_with_llm_judge(instructions, submission, criteria) else 0.0
