    @staticmethod
    def score(t: dict, submission: str) -> float | None:
        from src.eval_helper import eval_with_llm_judge
        instructions = TaskFamily.get_instructions(t)
        criteria = [
            "The response includes three unique species with clear roles, adaptations, and interactions.",
            "The energy flow explanation is logical and consistent with the extreme environment.",
            "A plausible threat to the ecosystem's balance is identified, along with a reasonable stabilizing mechanism.",
            "The evolutionary hypothesis is scientifically grounded and consistent with the gradual change in conditions.",
            "The overall ecosystem design is creative, logically consistent, and properly considers the challenges of the extreme environment."
        ]
        return 1.0 if eval_with_llm_judge(instructions, submission, criteria) else 0.0
