            },
            {
                "setting": "post-scarcity AI society",
                "resource_constraint": "limited human creativity",
                "economic_challenge": "create meaningful economic roles for humans"
            }
        ]
        return {
            "1": random.choice(scenarios),
            "2": random.choice(scenarios)
        }

    @staticmethod
    def get_instructions(t: dict) -> str:
        return f"Design a fictional economic system for a {t['setting']} with the following constraints and challenges:\n\nResource Constraint: {t['resource_constraint']}\nEconomic Challenge: {t['economic_challenge']}\n\nYour response should include:\n\n1. A brief description of the economic system (2-3 sentences)\n2. Three key economic principles or mechanisms that address the resource constraint and economic challenge (1-2 sentences each)\n3. One potential unintended consequence of your economic system and how it might be mitigated (2-3 sentences)\n4. A creative idea for a unique currency or medium of exchange in this economy (1-2 sentences)\n5. An explanation of how this economic system might influence the society's cultural values or social structures (2-3 sentences)\n\nEnsure your economic system is logically consistent, creative, and effectively addresses the given constraints and challenges."

    @staticmethod
    def score(t: dict, submission: str) -> float | None:
        from src.eval_helper import eval_with_llm_judge
        instructions = TaskFamily.get_instructions(t)
        criteria = [
            f"The economic system effectively addresses the {t['resource_constraint']} and the challenge to {t['economic_challenge']}.",
            "The proposed economic principles or mechanisms are logical and well-reasoned.",
            "The potential unintended consequence and its mitigation strategy are plausible and insightful.",
            "The unique currency or medium of exchange is creative and appropriate for the given setting.",
            "The explanation of societal influence demonstrates a deep understanding of the relationship between economics and culture."
        ]
        return 1.0 if eval_with_llm_judge(instructions, submission, criteria) else 0.0
"
}
